import React from "react";
import PropTypes from "prop-types";
import {
    Card,
    Grid
} from 'semantic-ui-react'

export default function FlagCard({children, user}) {
    return (
    <Card>
      <Card.Content>
        {children.map(child => React.cloneElement(child, { user }))}
        {countryCode && <img src={`https://www.countryflags.io/${countryCode.toLowerCase()}/shiny/64.png`} />}
      </Card.Content>
    </Card>
    )
}

FlagCard.propTypes = {
    children: PropTypes.node.isRequired,
    countryCode: PropTypes.string.isRequired
}
