import React from "react";
import PropTypes from "prop-types";
import {
    Card,
    Grid
} from 'semantic-ui-react'

export default function FlagCard({children, user}) {
    return (
    <Card>
      <Card.Content>
        {children.map(child => React.cloneElement(child, { user }))}
        <Grid>
            <Grid.Row>
                <Grid.Column>
                    {user.countryCode && <img src={`https://www.countryflags.io/${countryCode.toLowerCase()}/shiny/64.png`} />}
                </Grid.Column>
                <Grid.Column>
                    {user.content && <p>{user.content}</p>}
                </Grid.Column>
            </Grid.Row>
        </Grid>
      </Card.Content>
    </Card>
    )
}

FlagCard.propTypes = {
    children: PropTypes.node.isRequired,
    user: PropTypes.shape({
        email: PropTypes.string.isRequired,
        avatar: PropTypes.string.isRequired,
        jobTitle: PropTypes.string.isRequired,
        countryCode: PropTypes.string.isRequired,
    })
}
