import React from "react";
import PropTypes from "prop-types";
import {
    Card,
    Image
} from 'semantic-ui-react'

export default function FlagCard({children, countryCode}) {
    return (
    <Card>
      <Card.Content>
        {children}
        <img src={`https://www.countryflags.io/be/shiny/${countryCode}.png`} />
      </Card.Content>
    </Card>
    )
}

FlagCard.propTypes = {
    children: PropTypes.node.isRequired,
    countryCode: PropTypes.string.isRequired
}
